import React, { useContext } from "react";
import { useAccount, useDisconnect, useConnect } from 'wagmi';

import poolImage from '../../images/fixed pools.jpeg';
import StakeContext from "../../contextComponents/stakeContext/StakeContext";
import TokenContext from "../../contextComponents/tokensContext/TokenContext";

import '../fixedPools/FixedPool.css';

function FixedPools () {
    // wagmi hooks
    const { connect, connectors } = useConnect();
    const { address, isConnected } = useAccount();
    const { disconnect } = useDisconnect();

    // using context hook to approve tokens for staking 
    const approveTrans = useContext(TokenContext);
    const _approveToken = async () => {
        try {
            await approveTrans.approve1();
        } catch (e) {
            console.log(e);
        }
    }

    const _approve2Token = async () => {
        try {
            await approveTrans.approve2();
        } catch (e) {
            console.log(e);
        }
    }

    const _approve3Token = async () => {
        try {
            await approveTrans.approve3();
        } catch (e) {
            console.log(e);
        }
    }

    // staking the tokens in fixed pools
    const fixStakePoolTrans = useContext(StakeContext);
    const fixStakePool1 = async() => {
        try {
            await fixStakePoolTrans.fixStakes.fixStake1();
        } catch (e) {
            console.log(e);
        }
    }

    const fixStakePool2 = async() => {
        try {
            await fixStakePoolTrans.fixStakes.fixStake2();
        } catch (e) {
            console.log(e);
        }
    }

    const fixStakePool3 = async() => {
        try {
            await fixStakePoolTrans.fixStakes.fixStake3();
        } catch (e) {
            console.log(e);
        }
    }

    if(isConnected) {
        return (
            <div className="fixed-staking" >
            <div className="container right-display user-info">
            <p className="pt-5 pl-4">Signing in as {address}</p>
            <button className="btn1" onClick={() => disconnect()}>Disconnect</button>
            </div>

            <h1 className="fw-bolder py-4 mx-5 fix-pool">Welcome to the Fixed Pools Staking</h1>

            <div className="row justify-content-evenly">
                <div className="card col-3 mx-4 pool-1 pool">
                    <img className="mx-auto d-block" src={poolImage} />
                    {/* <img className="mx-auto d-block" src="data:image/png;base64," /> */}
                    <div className="card-body">
                        <h5 className="card-title fw-bolder">Bloom Pool</h5>
                        <p className="card-text ">Most affordable pool to stake with some nice reward after the 120 seconds!</p>
                        <div className="stake py-5">
                            <button className="btn" onClick={ _approveToken } >Approve 2 SHT Tokens to Stake</button>
                        <div className="py-2" />
                        <button className="btn mx-4" onClick={ fixStakePool1 }>Stake 2 SHT Tokens</button> </div>

                    </div>
                </div>

                <div className="card col-3 mx-4 pool-2 pool">
                    <img className="mx-auto d-block" src={poolImage} />
                    {/* <img className="mx-auto d-block" src="data:image/jpeg;base64," /> */}
                        <div className="card-body">
                        <h5 className="card-title fw-bolder">Thunder Pool</h5>
                        <p className="card-text">For more investing for more reward invested for the fixed time. Easy & Reliable!</p>
                        <div className="stake py-5"> <button className="btn" onClick={ _approve2Token }>Approve 4 SHT Tokens to Stake</button>
                        <div className="py-2" />
                        <button className="btn mx-4" onClick={ fixStakePool2 }>Stake 4 SHT Tokens</button> </div>

                    </div>
                </div>

                <div className="card col-3 mx-4 pool-3 pool">
                <img className="mx-auto d-block" src={poolImage} />
                    {/* <img className="mx-auto d-block" src="data:image/png;base64," /> */}
                        <div className="card-body">
                        <h5 className="card-title fw-bolder">Storm Pool</h5>
                        <p className="card-text">Most generous reward for the specified time investment. A secure Investment!</p>
                        <div className="stake py-5"> <button className="btn" onClick={ _approve3Token }>Approve 8 SHT Tokens to Stake</button>

                        <div className="py-2" />
                        <button className="btn mx-4" onClick={ fixStakePool3 }>Stake 8 SHT Tokens</button> </div>

                    </div>
                </div>
            </div>

            </div>
        );
    }
    else {
        return (
            <div className="container log-in">
                <h4 className="row fixed-pool pt-5 fw-bolder">Fixed Pools Staking</h4>
                <p className="fst-italic">Hey there I see you want to stake tokens in fixed stake to do it you must sign-in</p>

                <div className="image">
                    <img src="data:image/jpeg;base64," />

                </div>
                <div className="row pt-4 note">
                    <h1>Looking for Information!</h1>
                </div>

                <button className="connect-button" onClick={() => { connect({ connector: connectors[1] }) }}>Metamask</button>
                <button className="connect-button" onClick={() => { connect({ connector: connectors[0] }) }}>Coin Base</button>

                <h2 className="note pt-5 pl-5 fw-bolder">Connect the Wallet</h2>
            </div>
        );
    }

}

export default FixedPools;
