import React, { useEffect, useContext } from "react";
import '../flexiblePools/FlexiblePools.css';
import { useAccount, useDisconnect, useConnect } from 'wagmi';
import image from '/home/dev/Desktop/StakingProject/app-ui/src/images/download.jpeg';
import poolImage from '/home/dev/Desktop/StakingProject/app-ui/src/images/flexiblepools.png';
import { useContractWrite, usePrepareContractWrite, useWaitForTransaction } from 'wagmi';
import tokenAbi from "../../contractAbi/TokenAbi";
import StakeAbi from "/home/dev/Desktop/StakingProject/app-ui/src/contractAbi/StakingAbi.js";
import TokenContext from "../../contextComponents/tokensContext/TokenContext";
import StakeContext from "../../contextComponents/stakeContext/StakeContext";
// style={{ backgroundImage:`url(${image})`,backgroundRepeat:"no-repeat",backgroundSize:"cover",backgroundPosition: "center", position: "fixed",
// height:'100%',width:'100%'
// }}

function FlexiblePools () {
    // wagmi hooks
    const { connect, connectors } = useConnect();
    const { address, isConnected } = useAccount();
    const { disconnect } = useDisconnect();

    // using context hook to approve tokens for staking 
    const approveTrans = useContext(TokenContext);
    const _approveToken = async () => {
        try {
            await approveTrans.approve1();
        } catch (e) {
            console.log(e);
        }
    }

    const _approve2Token = async () => {
        try {
            await approveTrans.approve2();
        } catch (e) {
            console.log(e);
        }
    }

    const _approve3Token = async () => {
        try {
            await approveTrans.approve3();
        } catch (e) {
            console.log(e);
        }
    }

    // staking flexible pools
    const flexStakePoolTrans = useContext(StakeContext);
    const flexStakingPool1 = async() => {
        try {
            await flexStakePoolTrans.flexStakes.flexPool1();
        } catch (e) {
            console.log(e);
        }
    }

    const flexStakingPool2 = async() => {
        try {
            await flexStakePoolTrans.flexStakes.flexPool2();
        } catch (e) {
            console.log(e);
        }
    }

    const flexStakingPool3 = async() => {
        try {
            await flexStakePoolTrans.flexStakes.flexPool3();
        } catch (e) {
            console.log(e);
        }
    }

    if(isConnected) {
        return (
            <div className="fixed-staking">
            <div className="container right-display user-info">
            <p className="pt-5 pl-4">Signing in as {address}</p>
            <button className="btn1" onClick={() => disconnect()}>Disconnect</button>
            </div>
            <h1 className="fw-bolder py-4 mx-5 flex-pool">Welcome to the Flexible Pools Staking</h1>

            <div className="row justify-content-evenly">
                <div className="card col-3 mx-4 pool">
                    <img className="mx-auto d-block" src={poolImage} />
                    {/* <img className="mx-auto d-block" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRwZIUTuhBFIpw9Ep1bFPbZjOKzbbOLUaoOHmxF4rs8vR41oo9O7dvr8I_2R-4fMAaOAK4&usqp=CAU" /> */}
                    <div className="card-body">
                        <h5 className="card-title fw-bolder">Bronze Pool</h5>
                        <p className="card-text ">Most affordable pool to stake with some nice rewards. Enjoy rewards based on the stake-time!</p>
                        <div className="stake py-5"> <button className="btn" onClick={ _approveToken } >Approve 2 SHT Tokens to Stake</button>
                        <div className="py-2" />
                        <button className="btn mx-4" onClick={ flexStakingPool1 }>Stake 2 SHT Tokens</button> </div>
                    </div>
                </div>

                <div className="card col-3 mx-5 pool" >
                <img className="mx-auto d-block" src={poolImage} />
                    {/* <img className="mx-auto d-block" src="data:image/jpeg;base64," /> */}
                        <div className="card-body">
                        <h5 className="card-title fw-bolder">Golden Pool</h5>
                        <p className="card-text">Offering the rewards greater than those of Bronze Pool. A way to earn higher rewards!</p>
                        <div className="stake py-5"> <button className="btn" onClick={ _approve2Token }>Approve 4 SHT Tokens to Stake</button>
                        <div className="py-2" />
                        <button className="btn mx-4" onClick={flexStakingPool2}>Stake 4 SHT Tokens</button> </div>
                    </div>
                </div>

                <div className="card col-3 mx-5 pool">
                <img className="mx-auto d-block" src={poolImage} />
                    {/* <img className="mx-auto d-block" src="data:image/jpeg;base64," /> */}
                        <div className="card-body">
                        <h5 className="card-title fw-bolder">Platinum Pool</h5>
                        <p className="card-text">Most generous reward offerings made ever. Enjoy the staking & it's Rewards!</p>
                        <div className="stake py-5"> <button className="btn" onClick={ _approve3Token }>Approve 8 SHT Tokens to Stake</button>
                        <div className="py-2" />
                        <button className="btn mx-4" onClick={ flexStakingPool3 }>Stake 8 SHT Tokens</button> </div>
                    </div>
                </div>

            </div>
            </div>
        );
    }
    else {
        return (
            <div className="container log-in">
                <h4 className="row fixed-pool pt-5 fw-bolder">Flexible Pools Staking</h4>
                <p className="fst-italic">Hey there I see you want to stake tokens in flexible stake to do it you must 
                sign-in</p>

                <div className="image">
                    <img src="data:image/jpeg;base64," />

                </div>
                <div className="row pt-4 note">
                    <h1>Looking for Information!</h1>
                </div>

                <button className="connect-button" onClick={() => { connect({ connector: connectors[1] }) }}>Metamask</button>
                <button className="connect-button" onClick={() => { connect({ connector: connectors[0] }) }}>Coin Base</button>

                <h2 className="note pt-5 pl-5 fw-bolder">Connect the Wallet</h2>
            </div>
        );
    }

}

export default FlexiblePools;
